/*EXPECT_REGEX
^\s*(PHP )?Fatal error: Uncaught.*RuntimeException.*oops.*
*/

function a()
{
    time_nanosleep(0, 100000000);
    throw new RuntimeException('oops');
}

newrelic_record_datastore_segment(
    function () {
        time_nanosleep(0, 100000000);
    }, array(
        'product' => 'FakeDB',
    )
);
a();

echo 'this should never be printed';
